mapper.select:
SELECT id, sno, name, gender, password, portrait_path, email, telephone, address, introducation, clazz_name FROM tb_student
AND clazz_name = #{clazz_name}
AND name LIKE concat(concat('%',#{name}),'%')

mapper.update:
UPDATE tb_student
name=#{name}, gender=#{gender}, password=#{password}, email=#{email}, telephone=#{telephone}, address=#{address}, introducation=#{introducation}, portrait_path=#{portrait_path}, clazz_name=#{clazz_name},
WHERE id = #{id}

UPDATE tb_student
SET password=#{password}
WHERE id = #{id}
