mapper.select:
SELECT id, tno, name, gender, password, email, telephone, address, portrait_path, clazz_name FROM tb_teacher
AND clazz_name = #{clazz_name}
AND name LIKE concat(concat('%',#{name}),'%')

mapper.update:
UPDATE tb_teacher
name=#{name}, gender=#{gender}, password=#{password}, email=#{email}, telephone=#{telephone}, address=#{address}, portrait_path=#{portrait_path}, clazz_name=#{clazz_name},
WHERE id = #{id}

UPDATE tb_teacher
SET password=#{password}
WHERE id = #{id}
